def list_movie():
    for movie in movies:
        print(f'Gênero: {movie["genero"]}, Título: {movie["titulo"]}, Número: {movie["codigo"]}')

def pick_description(cod_movie):
    selected_movie = [movie for movie in movies if cod_movie == movie["codigo"]]
    sinopse = selected_movie[0]['sinopseFull']
    print(sinopse)

